import React, {Component} from "react"
import { connect } from "react-redux"
import ComedianPreview from "../Components/ComedianPreview"

class FavComics extends React.Component {

    render(){
        console.log("fav comics props", this.props)
        const favComics = this.props.comedians

        return (
            <>
            <h1>fav comics</h1>
            {this.props.currentUser.comedians == 0 ? 
                <p>no favorited comedians</p>
            : 
                favComics.map(comedian => <ComedianPreview key={comic.id} comedian={comedian}  />)
            }

            </>
        )
    }

}
const msp = state => {
    return {currentUser: state.currentUser}
}
export default connect(msp)(FavComics)
