import { ADD_SHOW, GET_SHOWS, ADD_SHOWS_FROM_FETCH, ADD_COMICS_FROM_FETCH} from "./actionTypes";

export const addShow = showObj => {
    console.log("addShow action", showObj)
    return function (dispatch) {
        fetch("http://localhost:3000/api/v1/events", {
            method: "POST",
            headers: {
                "content-type": "application/json",
                "accept": "application/json"
            },
            body: JSON.stringify({
                event: {
                    comedian_id: showObj.comedian_id,
                    date_time: showObj.date_time,
                    is_irl: showObj.is_irl,
                    is_virtual: showObj.is_virtual,
                    venue: showObj.venue,
                    address: showObj.address,
                    phone_number: showObj.phone_number,
                    comedian_id: showObj.comedi_id,
                    comedian_id: showObj.comedi_id,
                    comedian_id: showObj.comedi_id,
                }
            })
        })
            .then(resp=>resp.json())
            .then(data => dispatch({type: ADD_SHOW, payload: data}))

    }
}

export const getShows = () => {
    return function (dispatch) {
        fetch("http://localhost:3000/api/v1/events")
            .then(resp=>resp.json())
            .then(data => dispatch({type: ADD_SHOWS_FROM_FETCH, payload: data}))
    }
}
export const getComics = () => {
    return function (dispatch) {
        fetch("http://localhost:3000/api/v1/comedians")
            .then(resp=>resp.json())
            .then(data => dispatch({type: ADD_COMICS_FROM_FETCH, payload: data}))
    }
}



