import React, { Component } from "react"
import ComedianProfile from "../Components/ComedianProfile"
import { Route, Switch } from "react-router-dom"
import { connect } from 'react-redux'

function ComediansContainer (props) {

    return(

        <>
            <h1>Comedians Container</h1>

            {props.api.length === 0 ? <h1>LOADING</h1> :

                <Switch>
                    <Route path="/comedians/:id" render={({ match }) => {
                        let id = parseInt(match.params.id)
                        let foundComedian = props.api.find((comic) => comic.id === id)
                        return <ComedianProfile comedian={foundComedian} />
                    } } />
                    <Route path="/comedians" render={() => {
                        const comedians = props.api.map(comedianObj => <ComedianProfile key={comedianObj.id} id={comedianObj.id} comedian={comedianObj} name={comedianObj.name} website={comedianObj.personal_website} headshot={comedianObj.headshot} city={comedianObj.city} />)
                        return (
                            <>
                                {props.api.length === 0 ? <h1>LOADING</h1> : comedians }
                            </>
                        )
                    }} />
                </Switch>
            }

        </>

    )
}

export default ComediansContainer
