import { ADD_SHOW, GET_SHOWS } from "./actionTypes";

export const addShow = showObj => ({
    type: ADD_SHOW, payload: showObj
})

export const getShows = () => {
    return function (dipatch) {
        fetch("http://localhost:3000/api/v1/events")
            .then(resp=>resp.json())
            .then(data => dispatch({type: GET_SHOWS, payload: data}))
    }
}
